drop table if exists cliente;

-- Crear la tabla cliente
CREATE TABLE cliente (
    id INTEGER PRIMARY KEY,
    nombre VARCHAR(50) NOT NULL,
    apellido VARCHAR(50) NOT NULL,
    nacimiento DATE NOT NULL,
    genero VARCHAR(10) NOT NULL,
    empresa VARCHAR(50),
    idioma VARCHAR(50),
    nit VARCHAR(20),
    puesto VARCHAR(50),
    ciudad VARCHAR(50),
    correo VARCHAR(100),
    telefono VARCHAR(20)
);

